import 'package:flutter/material.dart';


void main() {
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'การใช้งาน Route',
      theme: ThemeData(
        primarySwatch: Colors.green,
      ),
      initialRoute: '/',
      routes: {
        '/': (context) => const Page1(title: "หน้าที่ 1"),
        '/Page2': (context) => Page2(title: "หน้าที่ 2"),
      },
    );
  }
}

class Page1 extends StatelessWidget {
  final String title;

  const Page1({super.key, required this.title});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(title),
      ),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            // การแสดงภาพจาก URL
            Image.network(
              'data:image/jpeg;base64,', // URL ของรูปภาพที่ต้องการแสดง
              height: 200, // กำหนดความสูงของรูปภาพ
              width: 200,  // กำหนดความกว้างของรูปภาพ
              fit: BoxFit.cover, // วิธีการครอบภาพ (จะบีบหรือขยายภาพตามขนาดที่กำหนด)
            ),
            const SizedBox(height: 20),
            ElevatedButton(
              onPressed: () {
                Navigator.pushNamed(context, '/Page2');
              },
              child: const Text("ไปที่หน้าที่ 2"),
            ),
          ],
        ),
      ),
    );
  }
}

class Page2 extends StatelessWidget {
  final String title;

  const Page2({super.key, required this.title});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(title),
      ),
      body: const Center(
        child: Text('นี่คือหน้าที่ 2'),
      ),
    );
  }
}
